import React, { useState, Suspense } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';
import Navbar from './components/Navbar/Navbar';
import Mainpage from './pages/Mainpage/Mainpage';
import Footer from './components/Footer/Footer';


// 페이지 컴포넌트
import Human from './pages/Letterpage/Human';
import Social from './pages/Letterpage/Social';
import Nature from './pages/Letterpage/Nature';


// Reacr.lazy를 사용하여 FullscreenMenu를 지연로드
const FullscreenMenu = React.lazy(() => import('./components/FullscreenMenu/FullscreenMenu'));

function App() {
  // 상태 관리: 메뉴 표시 여부와 버튼 숨김 여부
  const [isMenuVisible, setIsMenuVisible] = useState(false);

  // 토글 버튼 누르면 메뉴 보이기
  const openMenu = () => {
    setIsMenuVisible(true);
  };

  //메뉴 닫기 
  const closeMenu = () => {
    setIsMenuVisible(false);
  };
  function SwipeNavigator() {
    const navigate = useNavigate();
    const [startX, setStartX] = useState(0);
    const [isDragging, setIsDragging] = useState(false);

    // 터치 시작 이벤트 핸들러
    const handleTouchStart = (e) => {
      setStartX(e.touches[0].clientX);
      setIsDragging(true);
    };

    // 터치 이동 이벤트 핸들러
    const handleTouchMove = (e) => {
      if (!isDragging) return;
      const currentX = e.touches[0].clientX;
      const diffX = startX - currentX;

      // 스와이프 거리가 일정 기준 이상일 때 페이지 전환
      if (diffX > 50) { // 왼쪽으로 스와이프 (다음 페이지)
        navigate('/human'); // 원하는 경로로 이동 (예: '/human')
        setIsDragging(false);
      } else if (diffX < -50) { // 오른쪽으로 스와이프 (이전 페이지)
        navigate('/'); // 원하는 경로로 이동 (예: '/')
        setIsDragging(false);
      }
    };

    // 터치 끝 이벤트 핸들러
    const handleTouchEnd = () => {
      setIsDragging(false);
    };

    return (
      <div
        className="swipe-container"
        onTouchStart={handleTouchStart}
        onTouchMove={handleTouchMove}
        onTouchEnd={handleTouchEnd}
      >
        <Routes>
          <Route path="/" element={<Mainpage />} />
          <Route path="/human" element={<Human />} />
          {/* 다른 페이지 라우팅을 추가하세요 */}
        </Routes>
      </div>
    );
  }

  return (

      <Router>
        <div className="app">
          {/* 공통: 네비게이션 바 */}
          <Navbar onMenuClick={openMenu} />

          {/* 페이지 라우팅 설정 */}
          <Routes>
            <Route path="/" element={<Mainpage />} />
            <Route path="/human" element={<Human />} />
          </Routes>

          {/* 필요시-> 풀스크린 메뉴: Suspense로 감싸서 로딩 중 상태처리 */}
          {isMenuVisible && (
            <Suspense fallback={<div>Loading...</div>}>
              <FullscreenMenu onClose={closeMenu} />
            </Suspense>
          )}

          {/* 공통: footer 인스타 주소 */}
          <Footer />
        </div>
        <SwipeNavigator />

      </Router>


  );
}


export default App;
